import React, { useState } from 'react';
import { useKakaoLoader } from 'react-kakao-maps-sdk';
import './ControlPanel.css';

function ControlPanel({ mapCenter, setMapCenter, fetchRecommendations, loading }) {
  useKakaoLoader(); // 카카오맵 라이브러리 로딩을 기다림
  
  const [keyword, setKeyword] = useState(''); // 장소 검색 키워드
  const [recommendKeyword, setRecommendKeyword] = useState(''); // 우리 API용 키워드
  const [time, setTime] = useState('09:00'); // 시간 상태
  
  

  // 장소 검색 함수
  const searchPlace = () => {
    const places = new window.kakao.maps.services.Places();
    if (!keyword.trim()) {
      alert("검색할 장소를 입력하세요.");
      return;
    }
    places.keywordSearch(keyword, (data, status) => {
      if (status === window.kakao.maps.services.Status.OK) {
        const { y, x } = data[0]; // 첫 번째 검색 결과의 좌표
        const newCenter = { lat: parseFloat(y), lng: parseFloat(x) };
        setMapCenter(newCenter); // App.js의 지도 중심 상태 업데이트
      } else {
        alert("장소 검색에 실패했습니다.");
      }
    });
  };

  // 최종 추천 검색 실행 함수
  const handleRecommendSearch = () => {
    //const mapCenter = { lat: 37.773, lng: 128.889 }; // 임시: 현재 지도 중심 좌표를 가져와야 함
    fetchRecommendations(mapCenter.lat, mapCenter.lng, recommendKeyword, time);
  };

  return (
    <div className="control-panel">
      <div className="search-box">
        <input
          type="text"
          placeholder="출발 장소 검색 (예: 강릉역)"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && searchPlace()}
        />
        <button onClick={searchPlace}>위치 검색</button>
      </div>
      <div className="recommend-box">
        <input 
          type="text"
          placeholder="추천받을 키워드 (예: 카페)"
          value={recommendKeyword}
          onChange={(e) => setRecommendKeyword(e.target.value)}
        />
        <input 
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
        <button onClick={handleRecommendSearch} disabled={loading}>
          {loading ? '검색 중...' : '추천 검색'}
        </button>
      </div>
    </div>
  );
}

export default ControlPanel;
