import React, { useState } from 'react';
import './SIdePanel.css'; // 스타일링을 위한 CSS 파일

// 관광지 목록 아이템을 렌더링하는 자식 컴포넌트
function ResultItem({ item }) {
  // 거리(km)를 미터(m)로 변환
  const distanceInMeters = Math.round(item.distance * 1000);

  // 시간 계산 (시속 4km/h 도보 기준)
  // (거리(km) / 4) * 60분
  const travelTime = Math.ceil((item.distance / 4) * 60);

  return (
    <li className="list-item">
      <div className="item-info">
        <span className="item-name">{item.name}</span>
        <span className="item-details">
          거리: {distanceInMeters}m | 예상 시간: 약 {travelTime}분
        </span>
      </div>
      <button className="search-button">경로 검색</button>
    </li>
  );
}

// 메인 사이드 패널 컴포넌트
function SidePanel({ results }) {
  // 패널이 열려있는지 여부를 관리하는 상태
  const [isOpen, setIsOpen] = useState(false);

  const togglePanel = () => {
    setIsOpen(!isOpen);
  };

  return (
    // isOpen 상태에 따라 'open' 클래스를 동적으로 추가/제거
    <div className={`side-panel ${isOpen ? 'open' : ''}`}>
      {/* 패널을 열고 닫는 화살표 버튼 */}
      <div className="toggle-button" onClick={togglePanel}>
        {isOpen ? '›' : '‹'}
      </div>

      {/* API 결과 목록 */}
      <div className="panel-content">
        <h2>추천 관광지 목록</h2>
        {results.length > 0 ? (
          <ul className="results-list">
            {results.map((item, index) => (
              <ResultItem key={index} item={item} />
            ))}
          </ul>
        ) : (
          <p className="no-results">검색 결과가 없습니다.</p>
        )}
      </div>
    </div>
  );
}

export default SidePanel;
